function Pomodoro(o,t,i,s){this.duration=o,this.min_view=t,this.sec_view=i,this.pomodoroCounter_view=s,this.numberOfPomodoros=0,this.paused=!1;var e=this;this.timer=new Timer({tick:1,ontick:function(o){e.getTime(o)},onstart:function(){console.log("Timer started")},onstop:function(){console.log("Timer stop")},onpause:function(){e.onPause()},onend:function(){e.updatePomodoros()}}),this.getTime=function(o){var t=Math.floor(o/1e3/60%60);10>t&&(t="0"+t);var i=Math.floor(o/1e3%60);10>i&&(i="0"+i),$(this.min_view).html(t),$(this.sec_view).html(i)},this.start=function(){this.paused?(this.timer.start(),this.paused=!1):(console.log("The deed is done",this.duration),this.timer.start(this.duration))},this.updatePomodoros=function(){this.numberOfPomodoros++,console.log("Poms",this.numberOfPomodoros),$(this.pomodoroCounter_view).html(this.numberOfPomodoros),this.timer.stop()},this.onPause=function(){this.paused&&console.log("okay something happened?"),this.paused=!0,console.log("timer set on paused")},this.pause=function(){this.timer.pause()}}